--set serveroutput on
/* Select Ayaat to update urdu translation in soorah, ayat order */
SELECT a.ayat_id, a.ayat_text, a.translation_urdu 
FROM ayat a,
     soorah s
WHERE a.soorah_id = s.soorah_id 
AND soorah_seq_no = 2
AND ayat_seq_no >=31
AND ayat_seq_no <= 40;
--
--Grab ayat_ids for PL/SQL Block for root letter based translation update
SELECT s.soorah_seq_no, '   vtran('||TO_CHAR(a.ayat_id)||') := '''';', a.ayat_seq_no, a.ayat_text
FROM kalimaat_ayat_xref x,
    ayat a,
    soorah s,
    kalimaat k,
    root_letter r,
    arabic_alphabet ar 
WHERE a.ayat_id = x.ayat_id
AND a.soorah_id = s.soorah_id
AND k.kalimaat_id = x.kalimaat_id
AND k.root_letter_id = r.root_letter_id
AND ar.arabic_alphabet_id = r.arabic_alphabet_id
AND a.translation_urdu IS NULL
AND ROWNUM <= 10
ORDER BY ar.alphabet_text, r.root_letter_seq_no, k.kalimah_seq_no, s.soorah_seq_no, a.ayat_seq_no;
--
/*Update Ayat with urdu translation */
DECLARE    
