    i := vtran.FIRST;
    WHILE i IS NOT NULL LOOP
        UPDATE ayat 
        SET translation_urdu = vtran(i)
        WHERE ayat_id = i;
        i := vtran.NEXT(i);
    END LOOP;
END;
/
/*Update Ayat with urdu translation */

DECLARE    
    TYPE t_tran IS TABLE OF VARCHAR2(2000) INDEX BY PLS_INTEGER;
    vtran t_tran;
    i NUMBER;
BEGIN
    /*Replace index by ayat_id and text by translation */
    i := vtran.FIRST;
    WHILE i IS NOT NULL LOOP
        --DBMS_OUTPUT.PUT_LINE (TO_CHAR(i)||': '||vtran(i));
        UPDATE ayat 
        SET translation_urdu = vtran(i)
        WHERE ayat_id = i;
        i := vtran.NEXT(i);
    END LOOP;
END;
/
COMMIT;